import { React, useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import Axios from 'axios'

const Dashboard = () => {
    //store the user data
    const [employeeList, setEmployeeList] = useState([]);
    //destructuring the user data
    const { _id, firstName, lastName, email, contact, profession, salary } = employeeList;
    //navigate the page
    const history = useNavigate();
    //get the employee data
    useEffect(() => {
        Axios.get(`/dashboard`)
            .then((res) => {
                setEmployeeList(res.data)
            })
            .catch(err => {
                //not authenticated then navigate to login page
                console.log(err);
                history.push('/Login')
            })
    }, [])

    //delete the user
    const handleDelete = (id) => {
        Axios.delete(`/deleteUser/${id}`)
            .then(() => {
                window.location.reload();
            })
            .catch(err => {
                console.log("error" + err);
            })
    }
    return (
        <div className="das-main-div">
            <div>
                <div className="employeeData" >
                    <div className="employeeFeild">
                        <label>FirstName:-</label>
                        <p>{firstName}</p>
                    </div>
                    <div className="employeeFeild">
                        <label>LastName:-</label>
                        <p>{lastName}</p>
                    </div>
                    <div className="employeeFeild">
                        <label>Email:-</label>
                        <p>{email}</p>
                    </div>
                    <div className="employeeFeild">
                        <label>Contact:-</label>
                        <p>{contact}</p>
                    </div>
                    <div className="employeeFeild">
                        <label>Profession:-</label>
                        <p>{profession}</p>
                    </div>
                    <div className="employeeFeild">
                        <label>Salary:-</label>
                        <p>{salary}</p>
                    </div>
                    <div className="functionalButtons">
                        <NavLink to={`/editUser/:?id=${_id}`}>
                            <button
                                className="editButton"
                            // onClick={() => dispatch(selectEditList(id))}
                            >
                                EDIT
                            </button>
                        </NavLink>

                        <button
                            className="deleteButton"
                            onClick={() => handleDelete(_id)}
                        >
                            DELETE
                        </button>


                    </div>
                </div>
            </div>
        </div>
    )

};

export default Dashboard;
